export default {
    name: "FilterCard",
    data() {
        return{
            nutrientChoice: Number,
            amount: Number,
            buttonVal: ''
        }
    },

    methods: {
        generateAndConfirm (){
            this.$emit('confirm', this.filterArray)
        }
    },


    computed: {
         filterArray: function () {
             let returnArray = [];
             let returnObject = {};
             returnObject[this.nutrientChoice] = this.amount;
             returnArray.push(this.buttonVal);
             returnArray.push(returnObject);
             return returnArray
         }
    }
}
